'use strict';

const test = require('ava');
const utils = require('../lib/utils');

test('extrallendo hashtags de text', t => {
  let tags = utils.extractTags('a #imagenes con tags #AwEsOmE #Platzi #AVA and # #100 ##');

  t.deepEqual(tags, ['imagenes', 'awesome', 'platzi', 'ava', '100', 'yes']);

  tags = utils.extractTags('imagenes sin tags');
  t.deepEqual(tags, []);

  tags = utils.extractTags();
  t.deepEqual(tags, []);

  tags = utils.extractTags(null);
  t.deepEqual(tags, []);
});
